mapper.select:
SELECT NOTICE_NO AS noticeNo , APP_ID AS appId , NOTICE_TITLE AS noticeTitle , NOTICE_DT AS noticeDt , USER_ID AS userId , USER_NM AS userNm , PUSH_USE AS pushUse , WEB_USE AS webUse , SMS_USE AS smsUse , TOTAL_CNT AS totalCnt , PUSH_SUCCESS_CNT AS pushSuccessCnt , PUSH_FAIL_CNT AS pushFailCnt , PUSH_STATE AS pushState FROM T_PUSH_SEND_M
APP_ID = #{cond.appId}
AND (NOTICE_TITLE LIKE CONCAT('%', #{cond.keyword}, '%') OR USER_NM LIKE CONCAT('%', #{cond.keyword}, '%'))
AND PUSH_STATE = #{cond.pushState}
AND NOTICE_DT >= #{cond.fromDt}
AND NOTICE_DT <= #{cond.toDt}
ORDER BY NOTICE_NO DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM T_PUSH_SEND_M

    APP_ID = #{cond.appId}


        AND (NOTICE_TITLE LIKE CONCAT('%', #{cond.keyword}, '%')
        OR USER_NM    LIKE CONCAT('%', #{cond.keyword}, '%'))



        AND PUSH_STATE = #{cond.pushState}



        AND NOTICE_DT >= #{cond.fromDt}


        AND NOTICE_DT <= #{cond.toDt}
